mapper.select:
select rotMainId , rotMainTitle , rotMainType , rotMainSetUser , projectId , rotMainQuestionId , rotMainQuestionName , rotMainQuestionType , rotMainQuestionCheckNum , rotQuestionNames , rotFinalQuestionName , exitQuestionName , rotMMaxCount , regDate , delYn from sl_rotationMain
and projectId = #{projectId}
and rotMainId = #{rotMainId}
and rotMainType = #{rotMainType}
and rotMainQuestionId = #{rotMainQuestionId}
and rotQuestionNames like CONCAT('%',#{rotMainQuestionName},'%')

select rotPartId , rotMainId , rotPartTitle , rotPartOrder , projectId , regDate , delYn from sl_rotationPart
and projectId = #{projectId}
and rotMainId = #{rotMainId}

select rotId , rotPartId , rotTitle , projectId , rotQuestionId , rotQuestionName , rotQuestionType , rotSaveFileName , rotQuestionCheckNum , regDate , delYn from sl_rotationQuestion
and projectId = #{projectId}
and rotPartId = #{rotPartId}
and rotId = #{rotId}

select rotId , rotMainId , rotTitle , projectId , rotExampleId , rotExampleValue , rotExampleText , rotExampleType , rotExampleChecked , regDate , delYn from sl_rotationExample
and projectId = #{projectId}
and rotMainId = #{rotMainId}

mapper.update:
		update sl_rotationMain 


        		rotFinalQuestionName = #{rotFinalQuestionName},


        		exitQuestionName = #{exitQuestionName},


        		rotQuestionNames = #{rotQuestionNames},


		where projectId = #{projectId}
			and rotMainId = #{rotMainId}

		update sl_rotationPart 


        		rotMainId = #{rotMainId},


        		rotPartOrder = #{rotPartOrder},

      		regDate = sysdate()

		where projectId = #{projectId}
			and rotPartId = #{rotPartId}

		update sl_rotationQuestion set
        	rotPartId = 0,
      		regDate = sysdate()
		where projectId = #{projectId}
			and rotPartId = #{rotPartId}

mapper.delete:
		delete from sl_rotationPart
		where projectId = #{projectId} 

       		and rotMainId = #{rotMainId}


       		and rotPartId = #{rotPartId}

	delete from sl_rotationQuestion
	where projectId = #{projectId} and rotPartId = #{rotPartId}

       		and rotId = #{rotId}

